<script>
import ScrollPicker from 'vue3-scroll-picker';
export default {
    components: {
        ScrollPicker
    },
    data() {
        return {
            // scrollValue: 1,
            attackIsSelected: true,
            healIsSelected: false,
            selections: [1],
        }
    },
    methods: {
        selectAttack() {
            this.attackIsSelected = true
            this.healIsSelected = false
            this.$store.dispatch('setDamageActive',this.attackIsSelected)
        },
        selectHeal() {
            this.attackIsSelected = false
            this.healIsSelected = true
            this.$store.dispatch('setDamageActive',this.attackIsSelected)
        },
        wheelIt (event) {
            const deltaY = event.deltaY;
            if (deltaY > 0) {
                if (this.scrollValue > 1) {
                    this.scrollValue -= 1
                }
            } else if (deltaY < 0) {
                if (this.scrollValue < 200) {
                    this.scrollValue += 1
                }
            }
            // this.$store.dispatch('setScroll',this.scrollValue)
        }
    },
    computed: {
        scrollValue: {
            get () {
                return [this.$store.state.scrollValue]
            },
            set (value) {
                this.$store.dispatch('setScroll',value[0])
            }
        },
        damageActive: {
            get () {
                return this.$store.state.damageActive
            },
            set (value) {
                this.$store.dispatch('setDamageActive',value)
            }
        },
        myOptions() {
            var opt = [];
            for(let i = 100;i >= 1; i-- ) {
                opt.push({label:i,value:i});
            }
            return [opt]
        }
    }
  
}
</script>


